(window.webpackJsonp=window.webpackJsonp||[]).push([[25],{276:function(n,s,t){"use strict";t.r(s);var a=t(13),v=Object(a.a)({},(function(){var n=this,s=n._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[s("h1",{attrs:{id:"观看《功勋-于敏单元》感想"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#观看《功勋-于敏单元》感想"}},[n._v("#")]),n._v(" 观看《功勋-于敏单元》感想")]),n._v(" "),s("p",[s("strong",[n._v("多难殷忧兴国运，动心忍性希前哲")])]),n._v(" "),s("p",[n._v("——观看《功勋-于敏单元》感想")]),n._v(" "),s("p",[n._v("他，隐姓埋名28载，填补了我国原子核理论的空白，为氢弹突破作出了卓越贡献；\n他，不为外物所惑，不为权势所屈，不为利害所移，始终坚持淡泊明志，宁静致远的人生信条；\n他，就是国家最高科学技术奖、“两弹一星”功勋奖章、“共和国勋章”等多项荣誉的获得者——于敏！")]),n._v(" "),s("p",[n._v("1961年，在原子核理论研究取得突出成就之时，因为国家需要，毅然转入氢弹理论的研究，从此开启了隐姓埋名的生涯。\n多年艰苦卓绝的工作中，敢为天下先；科学计算与国际核心期刊不一致时，坚持真理不放松；就算一次又一次理论计算达不到目标，也始终不断的探索，不断的纠正，不断的前行。\n1967年6月17日，罗布泊沙漠腹地，随着一声巨响，一朵巨大无比的冷凝云在空中绽放。我国第一颗氢弹爆炸成功了！\n使我国真正突破了封锁，挺起了腰杆，站稳了脚跟。于敏及其团队成功了，他们的功勋震古烁今。")]),n._v(" "),s("p",[s("strong",[n._v("仰望历史的天空，家国情怀熠熠生辉")])]),n._v(" "),s("p",[n._v("在建国初期，我国尚处在一穷二白的阶段，于敏以及他的家人本来已准备好前往美国深造。这个时候换科研方向，放弃出国，可见是需要多么大的魄力。\n支撑这一选择的理由正是心中的家国情怀。伟大事业砥砺伟大精神，伟大精神引领伟大事业。只有把思想格局提升了，方能站得高，看得远。把一件事情从国家从历史的角度去思考，才能明确方向，心中坦然。")]),n._v(" "),s("p",[s("strong",[n._v("沧海横流方显英雄本色，风雨来袭更赖坚强砥柱")])]),n._v(" "),s("p",[n._v("一个有希望的民族不能没有英雄，一个有前途的国家不能没有先锋。\n于敏刚到新的研究单位，面对新环境的种种问题，依然坚定心中理想，所谓英雄者，坚强刚毅，屡败屡战，凡心所向，素履以往。\n于敏在研究时间非常紧迫的关键时刻，一次次可能走向失败的边缘时，夜以继日，顽强拼博，最终成功。所谓英雄者，挽狂澜于既倒，扶大厦于将倾。\n于敏开始研究的领域在国内还没有人研究，在国际上也没有可以参考的资料，完全从头开始，完成的结果是氢弹的结构命名为于敏构型，这种结构独步天下，无数国家举国之力都造不出来。所谓英雄者，敢为人之所不敢为，敢当人之所不敢当。")]),n._v(" "),s("p",[s("strong",[n._v("百舸争流，奋楫者先；中流击水，勇进者胜。")])]),n._v(" "),s("p",[n._v("时代在变，年轻的面孔也在变，但爱国和追求进步的目标永远不变。当前，我们国家处于百年未有之大变局中，在构建人类命运共同体的道路上依然有很多艰难挑战。中美关系大国博弈，牵动万家。我们在面对类似芯片这样的“卡脖子”问题时，唯有迎难而上、攻坚克难，最后一定能够解决。青春气贯长虹，永锐盖过怯弱，进取压倒苟安，奋斗两个字写在每一代中国青年的基因里。\n我们要以“千磨万击还坚劲”的顽强意志、“乱云飞渡仍从容”的战略定力、“独有英雄驱虎豹”的非凡气概、“弄潮儿向涛头立”的胆魄能力，披荆斩棘、乘风破浪，创造让世界刮目相看的新奇迹。")]),n._v(" "),s("p",[n._v("于高山之巅，方见大河奔涌；于群峰之上，更觉长风浩荡。\n最后，以“感动中国”组委会的颁奖词表达我对于敏院士的深切缅怀。")]),n._v(" "),s("div",{staticClass:"language- line-numbers-mode"},[s("pre",{pre:!0,attrs:{class:"language-text"}},[s("code",[n._v("离乱中寻觅一张安静的书桌，未曾向洋已经砺就了锋锷。\n受命之日，寝不安席，当年吴钩，申城淬火，十月出塞，大器初成。\n一句嘱托，许下了一生；一声巨响，惊诧了世界；一个名字，荡涤了人心。\n")])]),n._v(" "),s("div",{staticClass:"line-numbers-wrapper"},[s("span",{staticClass:"line-number"},[n._v("1")]),s("br"),s("span",{staticClass:"line-number"},[n._v("2")]),s("br"),s("span",{staticClass:"line-number"},[n._v("3")]),s("br")])])])}),[],!1,null,null,null);s.default=v.exports}}]);